var autoSolverTrail = [];
var solved = false;
function autoSolve(x, y) {
    if (solved) return;
    if (maze[y][x] === 1) return; //Can't go there...
    if (autoSolverTrail.find((item) => { return ((item.x === x) && (item.y === y)) })) return; //if we've already been here, don't go
    autoSolverTrail.push({x, y});
    if (maze[y][x] === 2) { solved = true; return; }
    if (x>0) autoSolve(x-1, y);
    if (x<(mazeSize-1)) autoSolve(x+1, y);
    if (y>0) autoSolve(x, y-1);
    if (y<(mazeSize-1)) autoSolve(x, y+1);
    if (!solved)
        autoSolverTrail.pop();
}

function drawSolver() {
    ctx.fillStyle = 'green';
    for (let i=0; i<autoSolverTrail.length; i++) {
        ctx.fillRect(autoSolverTrail[i].x*cellSize,autoSolverTrail[i].y*cellSize, cellSize, cellSize);
    }
}

function drawLoading() {   
    size = mazeSize*cellSize/3;
    ctx.fillStyle = 'red';
    ctx.fillRect(size, size, size, size);
    ctx.fillStyle = 'white';
    ctx.fillRect(size+1, size+1, size-2, size-2);
    ctx.font = "28px serif";
    ctx.fillStyle = 'black';
    ctx.fillText("LOADING...", size+15, size*1.5);

    ctx.font = "16px serif";
    ctx.fillText("Seconds: " + disSec, size + 15, size * 1.5 + 30);
    ctx.fillText("Minutes: " + disMin, size + 15, size * 1.5 + 50);
    ctx.fillText("Hours: " + disHour, size + 15, size * 1.5 + 70);
    ctx.fillText("Days: " + disDay, size + 15, size * 1.5 + 90);
    ctx.fillText("Years: " + disYear, size + 15, size * 1.5 + 110);
}

function randomInt(max) {
    return Math.round(Math.random()*max);
}

var attemptedSpots = [];
function newMaze() {
    ResetTime()
    autoSolverTrail = [];
    for (let y = 0; y < mazeSize; y++) {
        for (let x = 0; x < mazeSize; x++) {
            maze[y][x] = 1; //solid
        }
    }
    const finishY = randomInt(mazeSize-1); 
    maze[finishY][mazeSize-1] = 2; //finish

    ballPos.x = 0;
    ballPos.y = randomInt(mazeSize-1);
    maze[ballPos.y][ballPos.x] = 0; //start
    attemptedSpots = [ballPos];
    if (cutPath(ballPos.x+1, ballPos.y) == false) {
        newMaze(); //Not sure why, but sometimes if the Y dist is an odd number, a solution can't be found, so we just keep trying...
        //console.log(`unable to find a path: y dist: ${ballPos.y - finishY}`);
    }

    //find a random spot, and cut a path until it hits the existing path.
    for (var x=1;x<mazeSize-1;x++)
        for (var y=1;y<mazeSize-1;y++)
            if ((maze[y][x] === 1) &&
                (maze[y-1][x-1] === 1) &&
                (maze[y-1][x] === 1) &&
                (maze[y-1][x+1] === 1) &&
                (maze[y][x-1] === 1) &&
                (maze[y][x] === 1) &&
                (maze[y][x+1] === 1) &&
                (maze[y+1][x-1] === 1) &&
                (maze[y+1][x] === 1) &&
                (maze[y+1][x+1] === 1)) {
                attemptedSpots = [];
                cutExtraPath(x, y);
            }
}

function cutExtraPath(x, y) {
    //Repeats until it reachess the 2;  Won't go where there's a 0 (path already cut)
    if (attemptedSpots.find((spot) => (spot.x === x) && (spot.y === y))) return false;
    attemptedSpots.push({x:x, y:y});

    console.log(`Testing for out of bounds: (${x}, ${y})`);
    if (x <= 0) return false;
    if (y <= 0) return false;
    if (x >= (mazeSize-1)) return false;
    if (y >= (mazeSize-1)) return false;

    //Testing for adjacencies to real path
    console.log("Checking for adjacencies");
    var adjacencies = 0;
    if ((y>0) && (maze[y-1][x] === 0)) adjacencies++;
    if ((x>0) && (maze[y][x-1] === 0)) adjacencies++;
    if ((y<mazeSize-1) && (maze[y+1][x] === 0)) adjacencies++;
    if ((x<mazeSize-1) && (maze[y][x+1] === 0)) adjacencies++;
    if (adjacencies === 1) {
        maze[y][x]=0;
        return true;
    } else if (adjacencies > 1) return false;
    
    //Make sure we're not adjacent to ourselves more than once...
    adjacencies = 0;
    if (attemptedSpots.find((spot) => (spot.x === x) && (spot.y === y+1))) adjacencies++;
    if (attemptedSpots.find((spot) => (spot.x === x) && (spot.y === y-1))) adjacencies++;
    if (attemptedSpots.find((spot) => (spot.x === x+1) && (spot.y === y))) adjacencies++;
    if (attemptedSpots.find((spot) => (spot.x === x-1) && (spot.y === y))) adjacencies++;
    if (adjacencies > 1) {
        return false;
    }

    //attempted tries
    var triedLeft = false;
    var triedRight = false;
    var triedUp = false;
    var triedDown = false;

    while (!triedLeft || !triedRight || !triedUp || !triedDown) {
        switch(randomInt(3)) {
            case 0: if (x>1) if (cutExtraPath(x-1, y)) { maze[y][x]=0; return true; } triedLeft = true; break;
            case 1: if (x<mazeSize-2) if (cutExtraPath(x+1, y)) { maze[y][x]=0; return true; } triedRight = true; break;
            case 2: if (y>1) if (cutExtraPath(x, y-1)) { maze[y][x]=0; return true; } triedUp = true; break;
            case 3: if (y<mazeSize-2) if (cutExtraPath(x, y+1)) { maze[y][x]=0; return true; } triedDown = true; break;
        }
    }
    return false;
}

function cutPath(x, y, untilType = 2, maxAdjacent=1) {
    //Repeats until it reachess the 2;  Won't go where there's a 0 (path already cut)
    if (attemptedSpots.find((spot) => (spot.x === x) && (spot.y === y))) return false;
    attemptedSpots.push({x:x, y:y});
    if (untilType === 0) console.log(`Testing spot: (${x}, ${y})`);
    if (maze[y][x] === untilType) return true;
    
    if (x <= 0) return false;
    if (y <= 0) return false;
    if (x >= (mazeSize-1)) return false;
    if (y >= (mazeSize-1)) return false;
    if (untilType === 0) console.log(`looking for adjacent cell count...`);
    
    var adjacentPathTouches = 0;
    if ((y>0) && (maze[y-1][x] === 0)) adjacentPathTouches++;
    if ((y<mazeSize-1) && (maze[y+1][x] === 0)) adjacentPathTouches++;
    if ((x>0) && (maze[y][x-1] === 0)) adjacentPathTouches++;
    if ((x<mazeSize-1) && (maze[y][x+1] === 0)) adjacentPathTouches++;
    if (adjacentPathTouches > maxAdjacent) return false;

    if (untilType === 0) console.log(`Going in a random direction from here...`);

    maze[y][x]=0;

    //attempted tries
    var triedLeft = false;
    var triedRight = false;
    var triedUp = false;
    var triedDown = false;

    while (!triedLeft || !triedRight || !triedUp || !triedDown) {
        switch(randomInt(3)) {
            case 0: if (x>0) if (cutPath(x-1, y, untilType, maxAdjacent)) return true; triedLeft = true; break;
            case 1: if (x<mazeSize-1) if (cutPath(x+1, y, untilType, maxAdjacent)) return true; triedRight = true; break;
            case 2: if (y>0) if (cutPath(x, y-1, untilType, maxAdjacent)) return true; triedUp = true; break;
            case 3: if (y<mazeSize-1) if (cutPath(x, y+1, untilType, maxAdjacent)) return true; triedDown = true; break;
        }
    }

    maze[y][x] = 1; //reset this spot in the maze
    return false;
}

window.addEventListener("keyup", function keydown(e) {
    var keycode = e.which || window.event.keycode;
});

function mazeLoop() {
    requestAnimationFrame(mazeLoop);
    drawMaze();
    drawSolver();
    if (isLoading) drawLoading();
}

function UpdateTime() {
    seconds += 1;
    if (seconds == 60 || seconds >= 60) {
        minutes += 1;
        seconds = 0;
    }
    if (minutes == 60 || minutes >= 60) {
        hours += 1;
        minutes = 0;
    }
    if (hours == 24 || hours >= 24) {
        days += 1;
        hours = 0;
    }
    if (days == 365 || days >= 365) {
        years += 1;
        days = 0;
    }
}

function ResetTime() {
    seconds = 0;
    minutes = 0;
    days = 0;
    years = 0;
}

if (moved == false) {
    ResetTime()
}
setInterval(UpdateTime, 1000);
mazeLoop();